import React, { useEffect, useState } from 'react';

export default function Gramineas() {
  const [gramineas, setGramineas] = useState([]);
  const [nueva, setNueva] = useState({ nombre: '', caracteristicas: '', beneficios: '', temporadas: '', zonasRecomendadas: '', imagenUrl: '' });

  useEffect(() => {
    fetch('/api/gramineas')
      .then(res => res.json())
      .then(data => setGramineas(data));
  }, []);

  const handleChange = e => {
    setNueva({ ...nueva, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const res = await fetch('/api/gramineas', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(nueva)
    });
    if (res.ok) {
      const graminea = await res.json();
      setGramineas([...gramineas, graminea]);
      setNueva({ nombre: '', caracteristicas: '', beneficios: '', temporadas: '', zonasRecomendadas: '', imagenUrl: '' });
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Galería de Gramíneas</h2>
      <form onSubmit={handleSubmit} style={{ marginBottom: 20 }}>
        <input name="nombre" placeholder="Nombre" value={nueva.nombre} onChange={handleChange} required />
        <input name="caracteristicas" placeholder="Características" value={nueva.caracteristicas} onChange={handleChange} />
        <input name="beneficios" placeholder="Beneficios" value={nueva.beneficios} onChange={handleChange} />
        <input name="temporadas" placeholder="Temporadas" value={nueva.temporadas} onChange={handleChange} />
        <input name="zonasRecomendadas" placeholder="Zonas recomendadas" value={nueva.zonasRecomendadas} onChange={handleChange} />
        <input name="imagenUrl" placeholder="URL de imagen" value={nueva.imagenUrl} onChange={handleChange} />
        <button type="submit">Agregar Gramínea</button>
      </form>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: 16 }}>
        {gramineas.map(g => (
          <div key={g._id} style={{ border: '1px solid #ccc', borderRadius: 8, padding: 10, width: 220 }}>
            {g.imagenUrl && <img src={g.imagenUrl} alt={g.nombre} style={{ width: '100%', height: 100, objectFit: 'cover' }} />}
            <h4>{g.nombre}</h4>
            <p><b>Características:</b> {g.caracteristicas}</p>
            <p><b>Beneficios:</b> {g.beneficios}</p>
            <p><b>Temporadas:</b> {g.temporadas}</p>
            <p><b>Zonas:</b> {g.zonasRecomendadas}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
