import React, { useEffect, useState } from 'react';

export default function InventarioBovino() {
  const [bovinos, setBovinos] = useState([]);
  const [nuevo, setNuevo] = useState({ tipo: '', nombre: '', edad: '', raza: '', fechaNacimiento: '', fechaIngreso: '', procedencia: '', estadoSalud: '' });

  useEffect(() => {
    fetch('/api/bovinos')
      .then(res => res.json())
      .then(data => setBovinos(data));
  }, []);

  const handleChange = e => {
    setNuevo({ ...nuevo, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const res = await fetch('/api/bovinos', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(nuevo)
    });
    if (res.ok) {
      const bovino = await res.json();
      setBovinos([...bovinos, bovino]);
      setNuevo({ tipo: '', nombre: '', edad: '', raza: '', fechaNacimiento: '', fechaIngreso: '', procedencia: '', estadoSalud: '' });
    }
  };

  return (
    <div className="container">
      <h2>Inventario Bovino</h2>
      <form onSubmit={handleSubmit}>
        <select name="tipo" value={nuevo.tipo} onChange={handleChange} required>
          <option value="">Tipo</option>
          <option value="vaca">Vaca</option>
          <option value="toro">Toro</option>
        </select>
        <input name="nombre" placeholder="Nombre" value={nuevo.nombre} onChange={handleChange} />
        <input name="edad" placeholder="Edad" value={nuevo.edad} onChange={handleChange} />
        <input name="raza" placeholder="Raza" value={nuevo.raza} onChange={handleChange} />
        <input name="fechaNacimiento" type="date" value={nuevo.fechaNacimiento} onChange={handleChange} />
        <input name="fechaIngreso" type="date" value={nuevo.fechaIngreso} onChange={handleChange} />
        <input name="procedencia" placeholder="Procedencia" value={nuevo.procedencia} onChange={handleChange} />
        <input name="estadoSalud" placeholder="Estado de salud" value={nuevo.estadoSalud} onChange={handleChange} />
        <button type="submit">Agregar Bovino</button>
      </form>
      <ul>
        {bovinos.map(b => (
          <li key={b._id}>
            <b style={{color:'#1976d2'}}>{b.tipo}</b> - {b.nombre} | Raza: {b.raza} | Edad: {b.edad} | Estado: {b.estadoSalud}
          </li>
        ))}
      </ul>
    </div>
  );
}
