import React, { useEffect, useState } from 'react';

export default function Agenda() {
  const [eventos, setEventos] = useState([]);
  const [nuevoEvento, setNuevoEvento] = useState({ tipo: '', descripcion: '', fecha: '' });

  useEffect(() => {
    fetch('/api/eventos')
      .then(res => res.json())
      .then(data => setEventos(data));
  }, []);

  const handleChange = e => {
    setNuevoEvento({ ...nuevoEvento, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const res = await fetch('/api/eventos', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(nuevoEvento)
    });
    if (res.ok) {
      const evento = await res.json();
      setEventos([...eventos, evento]);
      setNuevoEvento({ tipo: '', descripcion: '', fecha: '' });
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Agenda de Actividades</h2>
      <form onSubmit={handleSubmit} style={{ marginBottom: 20 }}>
        <input name="tipo" placeholder="Tipo de evento" value={nuevoEvento.tipo} onChange={handleChange} required />
        <input name="descripcion" placeholder="DescripciÃ³n" value={nuevoEvento.descripcion} onChange={handleChange} />
        <input name="fecha" type="date" value={nuevoEvento.fecha} onChange={handleChange} required />
        <button type="submit">Agregar Evento</button>
      </form>
      <ul>
        {eventos.map(ev => (
          <li key={ev._id}>
            <b>{ev.tipo}</b> - {ev.descripcion} ({new Date(ev.fecha).toLocaleDateString()})
          </li>
        ))}
      </ul>
    </div>
  );
}
